package ciclos;

public class CicloFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<5;i++) {
			
			System.out.println("Hola " + i);
		}
	for(int i=5;i>0;i--) {
			
			System.out.println("Holax2 " + i);
		}
	
	//continue break 
	for (int i=0; i<10; i++) {
		
		if (i%2==0) {
		continue;
		}
	
	System.out.println(i);
	}

	}

}
